All framework configuration is done in the file  _config.js . Here you can load additional data, enabling the debug mode or load custom components.  lbs . externalConfig   =   { 

     /*      Enable or disable the debug-logging      */ 
     debug :   true , 

     /*      Verbose levels:          debug   :   Shows all log levels          info    :   Shows information level and up          warn    :   Shows warning level and up          error   :   Shows only error level logs      */ 
     verboseLevel :   'warn' , 

     /*      Load custom components      */ 
     components :   [ 
         {   name :   'my-component' ,   path :   'components/my-comp/my-comp.js'   } 
     ] 

     /*      Datasources to be used for each view      */ 
     config : { 
         helpdesk   :   { 
             dataSources :   [ 
                 {   type :   'activeLimeObject' ,   embed :   [ 'person' ]   }, 
                 {   type :   'translation'   }, 
                 {   type :   'relatedLimeObjects' ,   limetype :   'person' ,   alias :   'person'   }, 
             ] 
         } 
     }  }

lbs . apploader . register ( 'template' ,   function   ()   {   //Insert name of app here 
     var   self   =   this ; 

     //config 
     this . config   =   { 
         dataSources :   [   //Either provide your data source here, or let the user of your app supplie it 

         ], 
         resources :   {   //Add any extra resources that should be loadad. The paths are realtive your app folder, exept libs which are loaded from system/js/ 
             scripts :   [], 
             styles :   [ 'app.css' ], 
             libs :   [ 'json2xml.js' ] 
         } 
     }, 

     //initialize 
     this . initialize   =   function   ( node ,   viewModel )   { 

         //Use this method to setup you app. 
         // 
         //The data you requested along with activeInspector are delivered in the variable viewModel. 
         //You may make any modifications you please to it or replace is with a entirely new one before returning it. 
         //The returned viewmodel will be used to build your app. 


         return   viewModel ; 
     }
